using System;
using System.Collections.Generic;

namespace AdventOfCode.Event2016
{
    class Day02
    {
        string input =
@"



";

        public string Run()
        {
            string[] lines = input.Split(new[] { '\n', '\r' }, StringSplitOptions.RemoveEmptyEntries);
            // starting position
            int x = 0, y = 3;
            // keypad layout
            char[][] keypad = new[] {
                new[] { ' ', ' ', '1',' ',' ' },
                new[] { ' ', '2', '3','4',' ' },
                new[] { '5', '6', '7','8','9' },
                new[] { ' ', 'A', 'B','C',' ' },
                new[] { ' ', ' ', 'D',' ',' ' }
            };

            List<char> result = new List<char>();

            foreach (string line in lines)
            {
                foreach (char cmd in line)
                {
                    int newX = x, newY = y;
                    switch (cmd)
                    {
                        case 'U':
                            newY -= 1;
                            break;
                        case 'D':
                            newY += 1;
                            break;
                        case 'L':
                            newX -= 1;
                            break;
                        case 'R':
                            newX += 1;
                            break;
                    }

                    if (newY >= 0 && newY < keypad.Length && keypad[newY][x] != ' ')
                        y = newY;
                    if (newX >= 0 && newX < keypad[y].Length && keypad[y][newX] != ' ')
                        x = newX;
                }
                result.Add(keypad[y][x]);
            }
            return $"Keypad combo [{string.Join("", result)}]";
        }
    }
}
